package library.example;

import org.junit.Test;
import static org.junit.Assert.*;

public class LibraryTest {
    @Test public void testSomeLibraryMethod() {
        assertTrue("someLibraryMethod should return 'true'", Library.someLibraryMethod());
    }

    @Test
    public void testDoesItMakeGingerHappy() {
        assertTrue("everything should make Ginger happy",
                Library.doesItMakeGingerHappy("everything"));
    }

    @Test
    public void testDoesItMakeGingerHappy_sadThings() {
        assertFalse("cats should not make Ginger happy", Library.doesItMakeGingerHappy("Cats"));
        assertFalse("confinement should not make Ginger happy", Library.doesItMakeGingerHappy("Confinement"));
        assertFalse("no water should not make Ginger happy", Library.doesItMakeGingerHappy("No water"));
    }
}
